<template>
  <div class="head-wrap">
    <header>
      <div class="search-wrap">
        <div class="logo"></div>
        <form class="search" @click="handleClick" :class="isShow" action="javascript:void(0)">
          <i></i>
          <input type="text" placeholder="搜索" @keyup.enter="goSearch($event)" v-model="inputValue">
        </form>
      </div>
      <div v-if="isShow==='active'" @click="handleCancle">取消</div>
      <div class="rigth-background" v-else></div>
    </header>
  </div>
</template>

<script>
export default {
  data() {
    return {
      isShow: '',
      inputValue:''
    };
  },
  methods: {
    handleClick() {
      this.isShow = 'active';
    },
    handleCancle(){
      this.isShow = 'backActive';
    },
    async goSearch(){
      this.$router.push('/search/'+this.inputValue)
    }
  }
};
</script>

